import React, { useEffect, useState } from "react";
import {Link} from 'react-router-dom'

import Tuscany from '../DB/Tuscany.json';
import '../styles/Tour_Packages.css';
import arrow from '../assets/home/icons/arrow.png'
import clndr from '../assets/home/icons/clndr.svg'
import ppl from '../assets/home/icons/grp.png'
import specificTour from '../pages/SpecificTour';


export default function Tour_Packages () {
const [tours,setTours] = useState([]);
useEffect(() => {
    try{
        const response =  fetch('/tours');
        const data = response.json();
        setTours(data);
    }catch(error) {
        console.log(error);
    }
  tours();
},[tours])
