// //      let inputTextContent = event.target.value;
// //    if(inputTextContent.length > 24) {
// //     inputTextContent = inputTextContent.substring(0,24) + "...";*/
        <section className="tour_packages">
            
            {Tuscany.map((Tuscany)=> (
                
                 <section key={Tuscany.id}>
                    <img  src={Tuscany.image_url} alt={Tuscany.title}/> 
                    <p className="title"><strong>{Tuscany.title}</strong></p>
                    <p className="price"> from {Tuscany.price} </p>
                
                <section  className="tour">
            <section className="descrip">
                <img className="icon" src={clndr} alt="Choose a date"/>  <p> {Tuscany.visit} </p>       
                </section>

                <section className="tour">
                    <section className="descrip">
                      <img  className="icon "src={ppl} alt="choose a number of people"/> <p>{Tuscany.people} PP.</p>
                    </section>
                        </section>
                </section>
               
                <section className="description">
                    <p>{Tuscany.short_description}</p> 
                </section>
            
                <section >
                    <Link to="/specificTour" element={specificTour}>                    
                  <button className="btn">Read More <img src={arrow} alt="Read more"/></button>  
                    </Link>
                </section>
            </section>
       ))} 
       </section>
    )
}
