import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";

import ProgressBar from "../../components/ProgressBar";
import DatePicker from "../../Picker/DatePicker";
import TicketOverview from "../../components/TicketOverview";

import date from "../../assets/home/icons/Tours/date.svg";
import time from "../../assets/home/icons/Tours/time.svg";
import { useParams } from "react-router-dom";

import {
  setTourTitle,
  setAdultValue,
  setAdultPrice,
  setAdultCost,
  setChildValue,
  setChildCost,
  setInfantValue,
  setInfantPrice,
  setInfantCost,
  setTotalPrice,
  increaseAdultValue,
  decreaseAdultValue,
  increaseChildValue,
  decreaseChildValue,
  increaseInfantValue,
  decreaseInfantValue,
  setImgTour,
  setChildPrice,
} from "../../slices/tourSlice";
import { fetchTours } from "../../slices/fetchTours";

export default function BookingDetails() {
  const selectedTime = useSelector((state) => state.datepicker.selectedTime);
  const selectedDate = useSelector((state) => state.datepicker.selectedDate);

  const { id } = useParams();
  const dispatch = useDispatch();

  // useEffect(() => {
  //   if (id) {
  //     const numericId = Number(id);
  //     dispatch(setAdultPrice({ id: numericId }));
  //     dispatch(setChildPrice({ id: numericId }));
  //     dispatch(setInfantPrice({ id: numericId }));
  //     dispatch(setImgTour(numericId));
  //     dispatch(setTourTitle(numericId));
  //   }
  // }, [id, dispatch]);

  // const {
  //   adultValue,
  //   childValue,
  //   infantValue,
  //   adultPrice,
  //   childPrice,
  //   infantPrice,
  // } = useSelector((state) => state.tour);
  // useEffect(() => {
  //   dispatch(setAdultCost());
  //   dispatch(setChildCost());
  //   dispatch(setInfantCost());
  //   dispatch(setTotalPrice());
  // }, [
  //   adultValue,
  //   childValue,
  //   infantValue,
  //   adultPrice,
  //   childPrice,
  //   infantPrice,
  // ]);

//  useEffect(() => {
//   const bookingData = {
//     selectedDate,
//     selectedTime,
//     adultValue,
//     childValue,
//     infantValue,
//     childPrice,
//     adultPrice,
//     infantPrice,
//   };
const tours = useSelector((state) => state.fetchTours.tours);
const loading = useSelector((state) => state.fetchTours.loading);
console.log('Tours from Redux', tours);
console.log('Current tour ID', id);
useEffect(() => {
  if (id && tours.length > 0) {
    const numericId = Number(id);
    dispatch(setAdultPrice({ id: numericId, tours }));
    dispatch(setChildPrice({ id: numericId, tours }));
    dispatch(setInfantPrice({ id: numericId, tours }));
    dispatch(setImgTour({ id: numericId, tours }));
    dispatch(setTourTitle({ id: numericId, tours }));
  }
}, [id, tours, dispatch]);




// }, [selectedDate, selectedTime, adultValue, childValue, infantValue, childPrice, adultPrice, infantPrice]);

  // axios.post("http://localhost:3000/booking/details")
  //   .then(res => console.log("Booking sent:", res.data))
  //   .catch(err => console.error("Error sending booking:", err));

  return (
    <>
      <>
        <ProgressBar />
      </>
      <section className="booking_container">
        <section>
          {/*date
          <section className="booking_data">
            <h2>When you will visit?</h2>
            <section style={{ display: "flex" }}>
              <img src={date} alt="choose a data" />
              <select>
                <option>{selectedDate || "Choose a date"}</option>
              </select>
              {!selectedDate && <DatePicker />}
            </section>
          </section>
          {/*Time */}
          <section className="booking_data">
            <h2>Which time?</h2>
            <section>
              <img src={time} alt="choose a time" />{" "}
              <select>
                <option>{selectedTime || "Choose a time"}</option>
              </select>
              {/* {!selectedTime  && <TimePicker/>} */}
            </section>
          </section>
          {/* style={{ backgroundColor: "#EFEFEF", opacity: 0.4 }} */}

          <ul>
            <h2>Select Your Tickets</h2>
            <li>Free for kids under 6 and disabled visitors (74%+)</li>
            <li>
              Pregnant women, families with strollers, and visitors on crutches
              can buy priority tickets at the venue
            </li>
          </ul>

          <section className="tickets_body">
            {/*Adult */}
            <AdultTicket />
            {/*Child  */}
            <ChildTicket />
            {/*Infant */}
            <InfantTicket />
          </section>
        </section>
        <TicketOverview nextLink={"/User"} />
      </section> */}
    </>
  );
}

const Counter = ({ value, increase, decrease, setValue }) => {
  const dispatch = useDispatch();

  return (
    <section className="tickets_counter">
      <button
        style={{
          height: "3.125em",
          width: "3.125em",
          color: "#333333",
          backgroundColor: "transparent",
          border: "#EFEFEF 0.4em,",
        }}
        onClick={() => dispatch(decrease())}
      >
        {" "}
        -{" "}
      </button>

      <input
        style={{
          height: "3.125em",
          width: "3.125em",
          backgroundColor: "#EFEFEF",
        }}
        type="number"
        value={value}
        onChange={(e) => dispatch(setValue(Number(e.target.value)))}
      />

      <button
        style={{
          height: "3.125em",
          width: "3.125em",
          backgroundColor: "transparent",
          border: "#EFEFEF 0.4em,",
        }}
        onClick={() => dispatch(increase())}
      >
        {" "}
        +{" "}
      </button>
    </section>
  );
};

const AdultTicket = () => {
  const dispatch = useDispatch();
  const adultPrice = useSelector((state) => state.tour.adultPrice);
  const adultValue = useSelector((state) => state.tour.adultValue);

  return (
    <section className="tickets_order">
      <ul>
        <h2>Adult (18+)</h2>
       <h3 style={{ color: "#FA8B02" }}>
  €{adultPrice !== null && adultPrice !== undefined ? adultPrice : "Not available"}
</h3>


      </ul>

      <Counter
        value={adultValue}
        increase={() => dispatch(increaseAdultValue())}
        decrease={() => dispatch(decreaseAdultValue())}
        setValue={(val) => dispatch(setAdultValue(val))}
      />
    </section>
  );
};

const ChildTicket = () => {
  const childPrice = useSelector((state) => state.tour.childPrice);
  const childValue = useSelector((state) => state.tour.childValue);

  return (
    <section className="tickets_order">
      <ul>
        <h2>Child (6-17)</h2>
        <li>With valid ID</li>
        <li>Only in combination with: Adult (18+)</li>
        <h3 style={{ color: "#FA8B02" }}>€{childPrice || "Not available"}</h3>
      </ul>

      <Counter
        value={childValue}
        increase={increaseChildValue}
        decrease={decreaseChildValue}
        setValue={setChildValue}
      />
    </section>
  );
};

const InfantTicket = () => {
  const infantPrice = useSelector((state) => state.tour.infantPrice);
  const infantValue = useSelector((state) => state.tour.infantValue);

  return (
    <section className="tickets_order">
      <ul>
        <h2>Infant (0-5)</h2>
        <li>Only in combination with: Adult (18+)</li>
        <h3 style={{ color: "#FA8B02" }}>{infantPrice || "FREE"}</h3>
      </ul>

      <Counter
        value={infantValue}
        increase={increaseInfantValue}
        decrease={decreaseInfantValue}
        setValue={setInfantValue}
      />
    </section>
  );
};
