import React,{useContext, useState} from "react";
import time from '../assets/home/icons/Tours/time.svg';
import { ToursContext } from "../context/ToursContext";

export default function TimePicker () {
const [selectedTime,setSelectedTime] = useState(null)
    const {toursData,updateToursData} = useContext(ToursContext);

   
    const [hour,setHour] = useState(new Date().getHour);
    const [minute,setMinute] = useState(new Date().getMinute);
    
    const Time = [
     
        {id:0, label:"13:00", Date:`{hour(13)} + minute(00)`}
    ];
    const handleTimechange = (time) => {
        
        setSelectedTime(time);
        

    }

  
    return(
        <>
            <section className="custom_container">
                    <select onChange={handleTimechange}>
                        {Time.map((time) => (
                            <option key = {time.id} value = {time.value}>
                                {time.label}
                            </option>
                        ))}
                    </select>          
                                <img src={time}/>  
            </section>
        </>
    )
}
