import React, { useState } from "react";
import { useParams } from "react-router-dom";
import tour from "../DB/Tuscany.json";
import Modal from "./modal"; // Імпорт модального компонента

export const MainGallery = () => {
  const { id } = useParams();
  const [activeImage, setActiveImage] = useState(null); // Зберігає ID активного зображення для модального вікна

  const gallery = tour.find((gallery) => gallery.id === parseInt(id));

  // Перевірка, чи існує галерея
  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  const images = [
    { id: 0, src: gallery.img, alt: "Gallery place 0" },
    { id: 1, src: gallery.img1, alt: "Gallery place 1" },
    { id: 2, src: gallery.img2, alt: "Gallery Image 2" },
    { id: 3, src: gallery.img3, alt: "Gallery Image 3" },
  ];

  const validImages = images.filter((img) => img.id >= 0 && img.id <= 4); 

  const handleOpenModal = (id) => {
    if (id >= 0 && id <= 4) {
      setActiveImage(id);
    }
  };

  const handleCloseModal = () => {
    setActiveImage(null);
  };

  return (
    <>
     
    <section className="main_gallery" key={gallery.id}>
        <img
          src={gallery.img}
          alt="Gallery Image"
          onClick={() => handleOpenModal(images.id)} 
        />
        <section className="main_gallery_items">
          <img
           src={gallery.img1}
            alt="Gallery Image 1"
             />
          <img src={gallery.img2} alt="Gallery Image 2"
          onClick={() => handleOpenModal(images.id)}  />
          <img src={gallery.img3} alt="Gallery Image 3"
          onClick={() => handleOpenModal(images.id)}  />
        </section>
      </section>
    

      {activeImage !== null && (
        <Modal showModal={true} onClose={handleCloseModal}>
          <img
            src={validImages.find((img) => img.id === activeImage)?.src}
            alt="Selected Gallery Image"
          />
        </Modal>
      )}
    </>
  );
};
