import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// fetchTours now accepts an ID argument to fetch a specific tour or list
export const fetchTours = createAsyncThunk("tour/fetchTours", async (id) => {
  const response = await axios.get(`http://localhost:5000/tour/tours/id`);
 console.log("Response from backend:", response.data);
  return response.data;
});

const tourSlice = createSlice({
  name: "tour",
  initialState: {
    tours: [],
    ticketData: {
      adultValue: 0,
      childValue: 0,
      infantValue: 0,
      adultPrice: null,
      childPrice: null,
      infantPrice: 0,
      adultCost: 0,
      childCost: 0,
      infantCost: 0,
      totalPrice: 0,
      img: null,
      title: null,
      // Add other fields as needed
    },
  },
  reducers: {
    setAdultPrice: (state, action) => {
      const { id } = action.payload;
      const tour = state.tours.find((item) => item._id === id);
      state.ticketData.adultPrice = tour ? tour.adultPrice : null;
    },
    setChildPrice: (state, action) => {
      const { id } = action.payload;
      const tour = state.tours.find((item) => item._id === id);
      state.ticketData.childPrice = tour ? tour.childPrice : null;
    },
    setInfantPrice: (state, action) => {
      const { id } = action.payload;
      const tour = state.tours.find((item) => item._id === id);
      state.ticketData.infantPrice = tour ? tour.infantPrice : 0;
    },

    setAdultValue: (state, action) => {
      state.ticketData.adultValue = action.payload;
    },
    setChildValue: (state, action) => {
      state.ticketData.childValue = action.payload;
    },
    setInfantValue: (state, action) => {
      state.ticketData.infantValue = action.payload;
    },

    increaseAdultValue: (state) => {
      state.ticketData.adultValue += 1;
    },
    decreaseAdultValue: (state) => {
      if (state.ticketData.adultValue > 0) state.ticketData.adultValue -= 1;
    },
    increaseChildValue: (state) => {
      state.ticketData.childValue += 1;
    },
    decreaseChildValue: (state) => {
      if (state.ticketData.childValue > 0) state.ticketData.childValue -= 1;
    },
    increaseInfantValue: (state) => {
      state.ticketData.infantValue += 1;
    },
    decreaseInfantValue: (state) => {
      if (state.ticketData.infantValue > 0) state.ticketData.infantValue -= 1;
    },

    setImgTour: (state, action) => {
      const tour = state.tours.find((item) => item._id === action.payload);
      state.ticketData.img = tour ? tour.img : null;
    },
    setTourTitle: (state, action) => {
      const tour = state.tours.find((item) => item._id === action.payload);
      state.ticketData.title = tour ? tour.title : null;
    },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchTours.fulfilled, (state, action) => {
      state.tours = action.payload;
    });
  },
});

export const {
  setAdultPrice,
  setChildPrice,
  setInfantPrice,
  setAdultValue,
  setChildValue,
  setInfantValue,
  increaseAdultValue,
  decreaseAdultValue,
  increaseChildValue,
  decreaseChildValue,
  increaseInfantValue,
  decreaseInfantValue,
  setImgTour,
  setTourTitle,
} = tourSlice.actions;

export default tourSlice.reducer;
