import React, { useState } from "react";
import testimotional from '../DB/Testimotional.json';

export default function Testimotionals (){
// {
//   autoPlay :Boolean,
//   autoPlayTime:Number,
//   width:'50%',
//   height:'50%'
// }

const [activeIndex,setActiveIndex] = useState(1);
const nextSlide = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % testimotional.length );
}
const prevSlide = () => {
    setActiveIndex((prevIndex)=> (prevIndex - 1) % testimotional.length)
}
    return(
       <section >
        <section>
          
        </section>
        <button  onClick={prevSlide}>back</button>
        <button onClick={nextSlide}> go</button>
        <section className="testimotionals">
        {testimotional.map((testimonial, index) => (
          <section 
            key={testimonial.id}
            className="testimonial"
            // className={`testimonial ${index === activeIndex ? "active" : ""}`}
          >
            <img src={testimonial.image} alt={testimonial.name} />
            <h3 className="name">{testimonial.name}</h3>
            <h4 className="location">{testimonial.from}</h4>
            <p className="review">{testimonial.reviews}</p>
          </section>
        ))}
      </section>
        
       </section>
    )
}
