import React, { useState } from "react";
import testimotional from '../DB/Testimotional.json';
import back from '../assets/home/test_left.svg'
import right from '../assets/home/test_right.svg'

export default function Testimotionals (){

const [currentSlide,setCurrentSlide] = useState(0);


const handleNextSlide = () => {
  setCurrentSlide((prevSlide)=> prevSlide === testimotional.length -2 ? 0 : prevSlide + 1);
};
const handlePrevSlide = () => {
  setCurrentSlide((prevSlide) => (prevSlide === 0 ? testimotional.length - 1 : prevSlide - 1));

}

    return(
       <section >
        <section  className="btns">
          <button className="back" onClick={handlePrevSlide}><img src={back}/></button>
          <button className="next" onClick={handleNextSlide}><img src={right}/> </button>
        </section>
        <h1><strong>Happy Customers Says</strong></h1>
        <section className="testimotionals">
        {testimotional.map((testimonial, index) => (
          <section 
            key={testimonial.id}
            className={`testimonial ${index === currentSlide ? 'active' : ''}`}>
            <img className="test_img" src={testimonial.image} alt={testimonial.name} />
            <h3 className="name">{testimonial.name}</h3>
            <h4 className="location">{testimonial.from}</h4>
            <p className="review">{testimonial.reviews}</p>
          </section>
        ))}
      </section>
        
       </section>
    )
}
