import React, { useState } from "react";
import { useParams } from "react-router-dom";
import tour from "../DB/Tuscany.json";
import Modal from "./modal"; // Імпорт модального компонента

export const MainGallery = () => {
  const { id } = useParams();
  const [showModal, setShowModal] = useState(false);

  const gallery = tour.find((gallery) => gallery.id === parseInt(id));

  // Перевірка, чи існує галерея
  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  return (
    <>
    
      <section className="main_gallery" key={gallery.id}>
        
        <img
          src={gallery.img0}
          alt="Gallery Image"
          onClick={() => setShowModal(true)}
        />
        <section className="main_gallery_items">
         <img src={gallery.img1} alt="Gallery Image 1" onClick={() => setShowModal(true)} />
          <img src={gallery.img2} alt="Gallery Image 2"onClick={() => setShowModal(true)} />
          <img src={gallery.img3} alt="Gallery Image 3" onClick={() => setShowModal(true)}/>
        </section>
      </section>

      {/* Модальне вікно */}
      <Modal showModal={showModal} onClose={() => setShowModal(false)}>
        <div className="modal-gallery">
        
        </div>
      </Modal>
    </>
  );
};
