import React,{useState,useContext,useEffect} from "react";
import Tuscany from '../DB/Tuscany.json';


// import grp from '../assets/home/icons/grp.png';
import drtn from '../assets/home/icons/icons_tours/duration.svg'
import lctn from '../assets/home/icons/location.svg';
import gd from '../assets/home/icons/icons_tours/guide.svg'
import ln from '../assets/home/icons/icons_tours/language.svg'
import fs from '../assets/home/icons/icons_tours/ticket.svg'
// import trnsprt from '../assets/home/icons/icons_tours/transport.svg'
import DatePicker from "../components/DatePicker/DatePicker";
import { ToursContext } from "../context/ToursContext";



export default function SpecificTour ({id}) {   
    const [selectedTour, setSelectedTour] = useState([]);
  const toursContext = useContext(ToursContext);

  
  useEffect(() => {
    if (toursContext) {
      const tour = toursContext.find((tour) => tour.id === id);
      setSelectedTour(tour);
    }
  }, [toursContext, id]);
    // const [startDate, setStartDate] = useState(new Date());
    return(
        <section>
            <section >
                {Tuscany.map((selectedTour)=> (
                    <section key={selectedTour.id}>
                        <h3> {selectedTour.title}</h3>
                        <section className="price">
                            <p>from <p>{selectedTour.price}</p></p>
                        </section>
                            <p>{selectedTour.short_description}</p>

                            <lable>Date</lable>
                            <section className="date-picker">
                               <DatePicker/>
                                    
                            </section>
                
                            <lable>Time</lable>
                                <section>
                                      
                                        
                                </section>
                            <button> Buy Now</button>
                                <section>
                                    <h3>Details</h3>
                                    <p>{selectedTour.details}</p>
                                    <section>
                                        {/* <img src={grp} alt="numberOfPeople" /> <p> Number of group:{Tuscany.people}</p> */}
                                    </section>
                                    <section>
                                        <img src={drtn} alt="Duration of tour"/> <p> Duration:{selectedTour.duration}</p>
                                    </section>
                                    <section>
                                        <img src={lctn} alt="Departurting from"/> <p> Departuring and arriving areas:{selectedTour.departure}</p>
                                    </section>
                                    <section>
                                        <img src={gd} alt="Guide"/> <p> Guide service:{selectedTour.service}</p>
                                    </section>
                                    <section>
                                        <img src={ln}  alt="Language"/> <p> Language:{selectedTour.language}</p>
                                    </section>
                                    <section>
                                        <img src={fs} alt="Fees" /> <p> Entry Fees:{selectedTour.fees}</p>
                                    </section>
                                    <section>
                                        {/* <img src={trnsprt} alt="Transport" /><p> Entry Transportation:{Tuscany.transport}</p> */}
                                    </section>
                                </section>
                    </section>
    ))} 
            </section>  

              {/* <section className="Gallery">
               
              </section> */}
        </section>
    )
}
