import React, { createContext, useState } from "react";

export const ToursContext = createContext(null);

export const TourProvider = ({children}) => {
    const [toursData,setToursData] = useState({
        selectedDay:null,
     
        ticket:0,   selectedTime:null,
        price:null,


    });

    const updateToursData = (key,value) => {
        setToursData((prevData)=> ({...prevData,[key]:value}));
    };

        return(
            <ToursContext.Provider value={{toursData,updateToursData}}>
                {children}
            </ToursContext.Provider>
        )
}
