//             dispatch({type:'login',payload:json})
//             setIsLoading(false)
//         }


//     }
//     return{Login,isLoading,error};
// }


const [email,setEmail] = useState('');
const [password,setPassword] = useState('');

const handleSubmit = async(e) => {
    e.preventDefault()
    if(input.email !=="" && input.password !=="") {
        auth.loginAction(input);
    };

    console.log(email,password);
}
    return(
        <section className="auth">
           <form 
           onSubmit={handleSubmit}
           action="/login" method="post"> 
            <h1>Login</h1>
                
                    <label>Email Address</label>
                    <input 
                    type="email" 
                    placeholder="Enter your email address"
                    autoComplete="username"
                    required autoFocus
                    />
                    
                        <label>Password</label>
                        <input 
                        type="password" 
                        placeholder="Enter your password"
                        autoComplete="current-password"
                        required
                        onChange={(e)=> setPassword(e.target.value)}
                        value={password}
                        />
                      <Link to="/auth/resetpassword" element={<ResetPassword/>} >
                        <h5 className="password">Forgot your password?</h5>
                     </Link>
                    
                    <section className="form">
                    <button className="general_btn" type="submit" ><span>sign up</span> </button>
                        <p>or</p>
                        <Link to="/auth/google">
                       
                    <button className="btn_ggl_submit" type="submit" placeholder="Sign Up with Google"> 
                        <img src={ggl} alt="signIn with Google"/> 
                        <span>Sign In with Google</span>  
                    </button>
                    </Link>
                    </section>
                <section className="additionals">
                    <p>Already have an account?</p>
                <Link to='/auth/createAccount'element={<CreateAccount/>}>
                <span>Sign Up</span>
                </Link>
                </section>
                
                </form>
        </section>
    )
}
