import React,{useState,useContext, useEffect} from "react";
import { Link,useNavigate } from "react-router-dom";
import {useForm} from 'react-hook-form';
import ggl from '../../assets/home/icons/CheckIn/google.svg'
import ResetPassword from "./ResetPassword";
import CreateAccount from "./CreateAccount";
import { AuthContext } from "../../context/AuthContext.jsx";
// import useAuth from '../../context/AuthProvider';
import { useDispatch,useSelector } from "react-redux";
import {useLoginMutation} from '../../slices/usersApiSlice.js';
import { setCredentials } from "../../slices/authSlice.js";

export default function Login () {


const [email,setEmail] = useState('');
const [password,setPassword] = useState('');

const handleSubmit = async(e) => {
    e.preventDefault()
    try{
        const res = await login({email,password}).unwrap();
    }catch(error) {
        
    }
}

const navigate = useNavigate();
const dispatch = useDispatch;

const [login,{isLoading}] = useLoginMutation();

const {userInfo} = useSelector((state)=> state.auth);

useEffect(()=> {
    if(userInfo) {
        navigate('/')
    }
},[navigate,userInfo]);





//   const [user, setUser] = useContext(AuthContext);
//   const [token, setToken] = useState(localStorage.getItem("token") || "");
  
//     const navigate = useNavigate();

//   const loginAction = async (data) => {
//     try {
//       const response = await fetch("http://localhost:400/auth/login", {
//         method: "POST",
//         headers: {
//           "Content-Type": "application/json",
//         },
//         body: JSON.stringify(data),
//       });

//       const res = await response.json();

//       if (response.ok) {
//         setUser(res.user);
//         setToken(res.token);
//         localStorage.setItem("site", res.token);
//         navigate("/"); 
//         return;
//       }

//       throw new Error(res.message || "Login failed");
//     } catch (err) {
//       console.error("Login error:", err.message);
//       throw err; 
//     }
//   };


    return(
        <section className="auth">
           <form 
           onSubmit={handleSubmit}
           action="/login" method="post"> 
            <h1>Login</h1>
                    <label for="email">Email Address</label>
                    <input 
                    name="email"
                    type="email" 
                    value={email}
                    onChange={(e)=> setEmail(e.target.value)}
                    placeholder="Enter your email address"
                    autoComplete="username"
                    required autoFocus
                    />
                    
                        <label for="password"> Password</label>
                        <input
                        name="password" 
                        type="password" 
                        placeholder="Enter your password"
                        autoComplete="current-password"
                        required
                        onChange={(e)=> setPassword(e.target.value)}
                        value={password}
                        />
                      <Link to="/auth/resetpassword" element={<ResetPassword/>} >
                        <h5 className="password">Forgot your password?</h5>
                     </Link>
                    
                    <section className="form">
                    <button 
                      className="general_btn" 
                      type="submit"
                      
                    >
                      <span>sign up</span> </button>
                        <p>or</p>
                        <Link to="/auth/google">
                       
                    <button className="btn_ggl_submit" type="submit" placeholder="Sign Up with Google"> 
                        <img src={ggl} alt="signIn with Google"/> 
                        <span>Sign In with Google</span>  
                    </button>
                    </Link>
                    </section>
                <section className="additionals">
                    <p>Already have an account?</p>
                <Link to='/auth/createAccount'element={<CreateAccount/>}>
                <span>Sign Up</span>
                </Link>
                </section>
                
                </form>
        </section>
    )
}
