import React, { useState } from "react";


export default function BookingDetails() {
    const [ticket, setTicket] = useState(0);

    const increaseTicket = () => setTicket(ticket + 1);
    const decreaseTicket = () => setTicket(ticket > 0 ? ticket - 1 : 0);

    return (
        <>
             <section className="booking_container">
                <section className="booking_main">
                
                <section className="booking_details" >
                    <h2>When you will visit?</h2>
                   <select>
                    <option>

                    </option>
                   </select>
                </section>

                <section className="booking_details" >
                    <label><h2>Which time?</h2></label>
                   <select>
                    <option>

                    </option>
                   </select>
                   
                </section>
                <section>
                    
                    <ul><h2>Select Your Tickets</h2>
                        <li>Free for kids under 6 and disabled visitors (74%+) </li>
                        <li>Pregnant women, families with strollers, and visitors on crutches can buy priority tickets at the venue</li>
                    </ul>
                </section>
                <section className="tickets_order">
                    <h2>Adult(18+)</h2>
                    <h3></h3>
                    <section className="tickets_counter">
                        <button  
                        onClick={decreaseTicket}>-</button>
                        <input value={ticket} readOnly />
                        <button onClick={increaseTicket}>+</button>
                    </section>
                    <button >Add Ticket</button>
                </section>
                </section>
                 <section>
            <h1>Your Ticket Overview</h1>
            
            <div >
                    <p><strong>Tour:</strong> </p>
                    <p><strong>Date:</strong> </p>
                    <p><strong>Time:</strong> </p>
                    <p><strong>Price:</strong> â‚¬</p>
                </div>
        </section>  
            </section> 

        
        </>
    );
}
