import React from 'react';
// import { useParams } from 'react-router-dom';
// import { ToursContext } from '../context/ToursContext';
import tours from '../DB/Tuscany.json';

export default function SpecificTour({tours}) {
  const { id } = useParams(); // Get the tour ID from the URL

  const tour = tours.find(tour => tour.id === parseInt(id));

  if (!tour) {
    return <p>Tour not found.</p>;
  }


  return (
    <section>
      {tours?.map((tour,index)=> (
        <section 
        className="tour"
        key={tour.id}>
          <h2>{tour.title}</h2>
          <p>{tour.description}</p>
        </section>
      ))}
    </section>
  );
}
