import {createSlice} from '@reduxjs/toolkit';
import Tuscany from '../DB/Tuscany.json'
const tourSlice = createSlice({
  name: "tour",
  initialState: {
    adultPrice: 0,
    childPrice: 0,
    infantPrice: 0,
    adultCost: 0,
    childCost: 0,
    infantCost: 0,
    totalCost: 0,
    value: 0,
    
    img:null,

  },
  reducers: {
    setValue: (state, action) => {
      state.value = action.payload;
    },
    increase: (state) => {
      state.value += 1;
    },
    decrease: (state) => {
      if (state.value > 0) {
         state.value -= 1;
      } 
    },
    setAdultValue : (state) => {
      state.value = state.adultValue;
    },
    setAdultCost: (state, action) => {
      const findTour = Tuscany.find((tour) => tour.id === action.payload.id) || null;
      state.adultPrice = findTour ? findTour.adultPrice : 0;
      state.adultCost = action.payload.quantity * state.adultPrice;
    },
    
    setChildCost: (state, action) => {
      state.childPrice = Tuscany.find((price)=> price.childPrice === action.payload) || null;
      state.childCost = action.payload.quantity * state.childPrice;
    },
    setInfantCost: (state, action) => {
      state.infantPrice = Tuscany.find((price)=> price.infantPrice === action.payload) || null;
      state.infantCost = action.payload.quantity * state.infantPrice;
    },
    setTotalCost: (state) => {
      state.totalCost = state.adultCost + state.childCost + state.infantCost;
    },
    setImgTour :(state,action)=> {
      const findImgById = Tuscany.find((tour)=> tour.id === action.payload) || null;
      state.img = state.findImgById;
    },

  },
});

export const {
  setValue,
  increase,
  decrease,
  setAdultValue,
  setAdultCost,
  setChildCost,
  setInfantCost,
  setTotalCost,
  setImgTour
} = tourSlice.actions;
export default tourSlice.reducer;
