import React from "react";
import ProgressBar from '../../components/ProgressBar';
import data from '../../assets/home/icons/Tours/date.svg';
import time from '../../assets/home/icons/Tours/time.svg';
import Tuscany from '../../DB/Tuscany.json';
import { useDispatch,useSelector } from "react-redux";
import { Link } from "react-router-dom";
import DatePicker from "../../Picker/DatePicker";
import TimePicker from "../../Picker/TimePicker";
import { decrease,increase,setValue,setAdultValue,
  setChildValue,
  setInfantValue,
  increaseAdultValue,
  decreaseAdultValue,
  increaseChildValue,
  decreaseChildValue,
  increaseInfantValue,
  decreaseInfantValue,} from "../../slices/tourSlice";



export default function BookingDetails () {
	const dispatch = useDispatch();
	const selectedTime = useSelector((state) => state.datepicker.selectedTime);
	const selectedDate = useSelector((state) => state.datepicker.selectedDate);
    return(
		<>
			<ProgressBar/>
				<section className="booking_container">
					
						
						{/*Calendar */}
							<section className="booking_data" >
								<h2>When you will visit?</h2> 
								<section>             
									<img src={data} alt="choose a data"/>	
										<select> 
											<option>
												{selectedDate || 'Choose a date'}
											</option>
										</select>
										{!selectedDate && <DatePicker/>}
									</section> 
							</section>
						{/*Time */}
							<section className="booking_data" >
								<h2>Which time?</h2>
								<section>
									<img src={time} alt="choose a time"/>	<select>  
											<option>
												{selectedTime || 'Choose a time'}
											</option>
										</select>
										{!selectedTime  && <TimePicker/>}
								</section>
								</section>
							{/* style={{ backgroundColor: "#EFEFEF", opacity: 0.4 }} */}

							<section >
							<ul >
								<h2>Select Your Tickets</h2>
									<li>Free for kids under 6 and disabled visitors (74%+)</li>
									<li>Pregnant women, families with strollers, and visitors on crutches can buy priority tickets at the venue</li>
							</ul>	
							</section>
							<section className="tickets_body">
								{/*Adult */}
										<AdultTicket/>
								{/*Child  */}
								<ChildTicket/>
									{/*Infant */}
									<InfantTicket/>
							</section>
							<Ticket/>
						</section>

		</>
    )
}

const Counter = ({ value, increase, decrease, setValue }) => {
  const dispatch = useDispatch();

  return (
    <section className="tickets_counter">
<button onClick={() => dispatch(decrease())}> - </button>

      <input
        type="number"
        value={value}
        onChange={(e) => dispatch(setValue(Number(e.target.value)))}
      />

      <button onClick={() => dispatch(increase())}> + </button>
    </section>
  );
};


const AdultTicket = () => {
  const dispatch = useDispatch();
  const adultPrice = useSelector((state) => state.tour.adultPrice);
  const adultValue = useSelector((state) => state.tour.adultValue);

  return (
    <section className="tickets_order">
      <ul>
        <h2>Adult (18+)</h2>
        <h3>{adultPrice || "Not available"}</h3>
      </ul>

      <Counter
        value={adultValue}
        increase={increaseAdultValue}
