import { createSlice } from '@reduxjs/toolkit';


const ticketPayment = createSlice({
  name: "payment",
  initialState: {
    cardValue: "",
    paymentType: "",

  },
  reducers: {
    setPaymentMethod: (state, action) => {
      state.paymentType = action.payload;
    },
  }
})
export default { setPaymentMethod } = paymentSlice.reducer;
