export const TourProvider = ({ children }) => {
    const [toursData, setToursData] = useState({
        ticket: {
            adult: 0,
            child: 0,
            infant: 0,
        },
        selectedDay: null,
        selectedTime: null,
        adultPrice: tours.adultPrice,
        childPrice: tours.childPrice,
        infantPrice: tours.infantPrice,
        title: tours.title,
        img: tours.img,
    });

    // Оновлення квитків за типом
    const updateTickets = (type, value) => {
        setToursData((prevData) => ({
            ...prevData,
            ticket: {
                ...prevData.ticket,
                [type]: Math.max(value, 0), // Запобігання від’ємним значенням
            },
        }));
    };

    return (
        <ToursContext.Provider value={{ toursData, updateTickets }}>
            {children}
        </ToursContext.Provider>
    );
};
