import React, { useState } from "react";
import arr_left from '../../assets/home/test_left.svg';
import arr_r from '../../assets/home/test_right.svg';

export default function Calendar() {
  const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
  const months = ['January', 'February', 'March', 'April', 'May', 'June',
                  'July', 'August', 'September', 'October', 'November', 'December'];

  // State for the current date
  const [currentDay, setCurrentDay] = useState(new Date());

  // Get the first day of the current month
  const firstDayOfMonth = new Date(currentDay.getFullYear(), currentDay.getMonth(), 1);
  const weekdayOfFirstDay = firstDayOfMonth.getDay();
  const currentDays = [];

  // Generate days for the calendar
  for (let day = 0; day < 42; day++) {
    if (day === 0 && weekdayOfFirstDay === 0) {
      firstDayOfMonth.setDate(firstDayOfMonth.getDate() - 7);
    } else if (day === 0) {
      firstDayOfMonth.setDate(firstDayOfMonth.getDate() - weekdayOfFirstDay);
    } else {
      firstDayOfMonth.setDate(firstDayOfMonth.getDate() + 1);
    }

    const calendarDay = {
      currentMonth: (firstDayOfMonth.getMonth() === currentDay.getMonth()),
      date: new Date(firstDayOfMonth),
      month: firstDayOfMonth.getMonth(),
      selected: (firstDayOfMonth.toDateString() === currentDay.toDateString()),
      number: firstDayOfMonth.getDate(),
      year: firstDayOfMonth.getFullYear()
    };

    currentDays.push(calendarDay);
  }

  const changeCurrentDay = (day) => {
    setCurrentDay(day.date);
  };

  return (
    <section className="calendar">
      <section className="calendar-header">
        <h3>{months[currentDay.getMonth()]} {currentDay.getFullYear()}</h3>
        <section>
          <button><img src={arr_left} alt="Previous Month" /></button>
          <button><img src={arr_r} alt="Next Month" /></button>
        </section>
      </section>

      <section className="calendar-subheader">
        {daysOfWeek.map((day, index) => (
          <p key={index}>{day}</p>
        ))}
      </section>

      <section className="calendar-days">
        {currentDays.map((day, index) => (
          <section 
            key={index}
            className={"calendar-day " + 
              (day.currentMonth ? "current " : "") + 
              (day.selected ? "selected " : "")
            }
            onClick={() => changeCurrentDay(day)}
          >
            {day.number}
          </section>
        ))}
      </section>
    </section>
  );
}
