import React, { useState } from "react";
import { useParams } from "react-router-dom";
import tour from "../DB/Tuscany.json";
import Modal from "./ьodal"; // Імпорт модального компонента

export const MainGallery = () => {
  const { id } = useParams();
  const [showModal, setShowModal] = useState(false);
  const [activeImage, setActiveImage] = useState(null); // Для зберігання активного зображення

  const gallery = tour.find((gallery) => gallery.id === parseInt(id));

  // Перевірка, чи існує галерея
  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  const images = [gallery.img, gallery.img1, gallery.img2, gallery.img3]; // Масив усіх зображень

  return (
    <>
      {/* Основна галерея */}
      <section className="main_gallery" key={gallery.id}>
        {images.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Gallery Image ${index + 1}`}
            onClick={() => {
              setActiveImage(index); // Встановити активне зображення
              setShowModal(true); // Відкрити модальне вікно
            }}
          />
        ))}
      </section>

      {/* Модальне вікно */}
      <Modal showModal={showModal} onClose={() => setShowModal(false)}>
        <div className="modal-gallery">
          {activeImage !== null && (
            <>
              <img
                src={images[activeImage]}
                alt={`Gallery Image ${activeImage + 1}`}
              />
              <div className="modal-navigation">
                <button
                  onClick={() =>
                    setActiveImage((prev) =>
                      prev > 0 ? prev - 1 : images.length - 1
                    )
                  }
                >
                  Previous
                </button>
                <button
                  onClick={() =>
                    setActiveImage((prev) =>
                      prev < images.length - 1 ? prev + 1 : 0
                    )
                  }
                >
                  Next
                </button>
              </div>
            </>
          )}
        </div>
      </Modal>
    </>
  );
};
