import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import PhotoSwipeLightbox from "photoswipe/lightbox";
import "photoswipe/style.css";
import tour from "../DB/Tuscany.json";
import leftarrow from "../assets/home/icons/test_left.svg";
import rightarr from "../assets/home/icons/test_right.svg";

export const GalleryPlaces = () => {
  const { id } = useParams(); 

  useEffect(() => {
    let lightbox = new PhotoSwipeLightbox({
      gallery: "#carousel_container",
      children: "a",
      pswpModule: () => import("photoswipe"),
    });

    lightbox.init();

    return () => {
      lightbox.destroy();
      lightbox = null;
    };
  }, []);

  const gallery = tour.find((gallery) => gallery.id === id); // Порівнюємо як рядок

  if (!gallery) {
    return <p>Gallery not found</p>;
  }

  const { img4, img5, img6, img7 } = gallery;

  return (
    <section>
      <section className="carousel_header">
        <h3>Gallery</h3>
        <section className="arrows">
          <button className="prev btn">
            <img src={leftarrow} alt="back to previous place" />
          </button>
          <button className="next btn">
            <img src={rightarr} alt="go to the next place" />
          </button>
        </section>
      </section>

      <section className="carousel_container pswp-gallery" id="carousel_container">
        <a href={img4} data-pswp-width="800" data-pswp-height="600" target="_blank" rel="noreferrer">
          <img className="carousel_item large" src={img4} alt="place 1" />
        </a>
        <a href={img5} data-pswp-width="800" data-pswp-height="600" target="_blank" rel="noreferrer">
          <img className="carousel_item top-small" src={img5} alt="place 2" />
        </a>
        <a href={img6} data-pswp-width="800" data-pswp-height="600" target="_blank" rel="noreferrer">
          <img className="carousel_item bottom-small" src={img6} alt="place 3" />
        </a>
        <a href={img7} data-pswp-width="800" data-pswp-height="600" target="_blank" rel="noreferrer">
          <img className="carousel_item full" src={img7} alt="place 4" />
        </a>
      </section>
    </section>
  );
};
